import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
    PieChart, Pie, Cell, Legend
} from 'recharts';
import { TrendingUp, PieChart as PieChartIcon } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import '../styles/Analytics.css';
import ReactPaginate from 'react-paginate'

const Analytics = () => {
    const [transactions, setTransactions] = useState([]);
    const [budgetData, setBudgetData] = useState({ initial_budget: 0 });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [currentGraph, setCurrentGraph] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('authToken');
                if (!token) {
                    navigate('/login');
                    return;
                }

                const axiosInstance = axios.create({
                    baseURL: 'http://localhost:5000',
                    headers: { Authorization: `Bearer ${token}` },
                });

                const [transactionsRes, budgetRes] = await Promise.all([
                    axiosInstance.get('/transactions'),
                    axiosInstance.get('/budget')
                ]);

                setTransactions(transactionsRes.data);
                setBudgetData(budgetRes.data);
                setLoading(false);
            } catch (error) {
                setError(error.response?.data?.error || 'Failed to load data');
                setLoading(false);
            }
        };

        fetchData();
    }, [navigate]);

    // Prepare timeline data
    const timelineData = (() => {
        let currentBudget = budgetData.initial_budget;
        return transactions
            .sort((a, b) => new Date(a.date) - new Date(b.date))
            .map(t => {
                currentBudget += t.amount;
                return {
                    date: new Date(t.date).toLocaleDateString(),
                    budget: currentBudget,
                    transaction: t.description
                };
            });
    })();

    const categoryData = (() => {
        const categoryTotals = transactions.reduce((acc, t) => {
            if (t.amount < 0) {
                const category = t.category || 'Other';
                acc[category] = (acc[category] || 0) + Math.abs(t.amount);
            }
            return acc;
        }, {});

        return Object.entries(categoryTotals)
            .map(([name, value]) => ({ name, value }))
            .sort((a, b) => b.value - a.value);
    })();

    // Calculate stats...

    if (loading) return <div className="loading-container">Loading...</div>;
    if (error) return <div className="error-container">{error}</div>;

    const COLORS = ['#751d6c', '#e74c3c', '#27ae60', '#3498db', '#f1c40f'];
    
    const graphs = [
        {
            title: "Budget Timeline",
            icon: <TrendingUp size={24} />,
            content: (
                <div className="chart-wrapper">
                    <ResponsiveContainer width="100%" height={400}>
                        <LineChart data={timelineData} margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
                            <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                            <XAxis 
                                dataKey="date" 
                                tick={{ fill: '#2c3e50' }}
                                fontSize={12}
                            />
                            <YAxis 
                                width={80}
                                tickFormatter={value => `$${value}`}
                                tick={{ fill: '#2c3e50' }}
                                fontSize={12}
                            />
                            <Tooltip 
                                contentStyle={{
                                    background: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
                                }}
                                content={({ active, payload }) => {
                                    if (active && payload && payload.length) {
                                        return (
                                            <div className="custom-tooltip">
                                                <p>Date: {payload[0].payload.date}</p>
                                                <p>Budget: ${payload[0].value.toFixed(2)}</p>
                                                <p>Transaction: {payload[0].payload.transaction}</p>
                                            </div>
                                        );
                                    }
                                    return null;
                                }}
                            />
                            <Line 
                                type="monotone" 
                                dataKey="budget" 
                                stroke="#751d6c" 
                                strokeWidth={2}
                                dot={false}
                                activeDot={{ r: 6, stroke: '#751d6c', strokeWidth: 2 }}
                            />
                        </LineChart>
                    </ResponsiveContainer>
                </div>
            )
        },
        {
            title: "Expense Categories",
            icon: <PieChartIcon size={24} />,
            content: (
                <div className="chart-wrapper">
                    <ResponsiveContainer width="100%" height={400}>
                        <PieChart>
                            <Pie
                                data={categoryData}
                                cx="50%"
                                cy="50%"
                                innerRadius={60}
                                outerRadius={120}
                                paddingAngle={5}
                                dataKey="value"
                                labelLine={false}
                                label={({ name, percent }) => 
                                    percent > 0.05 ? `${name} (${(percent * 100).toFixed(0)}%)` : ''
                                }
                            >
                                {categoryData.map((entry, index) => (
                                    <Cell 
                                        key={`cell-${index}`} 
                                        fill={COLORS[index % COLORS.length]} 
                                    />
                                ))}
                            </Pie>
                            <Tooltip 
                                formatter={(value) => `$${Number(value).toFixed(2)}`}
                                contentStyle={{
                                    background: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
                                }}
                            />
                            <Legend 
                                verticalAlign="bottom"
                                align="center"
                                layout="horizontal"
                            />
                        </PieChart>
                    </ResponsiveContainer>
                </div>
            )
        }
    ];

    return (
        <div className="container">
            <div className="analytics-layout">
                <div className="main-content">
                    <motion.div 
                        className="graph-card"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5 }}
                    >
                        <div className="graph-header">
                            <div className="graph-title">
                                {graphs[currentGraph].icon}
                                <h2>{graphs[currentGraph].title}</h2>
                            </div>
                        </div>

                        <div className="graph-content">
                            {graphs[currentGraph].content}
                        </div>

                        <div className="pagination-container">
                            <ReactPaginate
                                previousLabel="Previous"
                                nextLabel="Next"
                                pageCount={graphs.length}
                                onPageChange={({ selected }) => setCurrentGraph(selected)}
                                containerClassName="pagination"
                                activeClassName="active"
                                previousClassName={currentGraph === 0 ? "disabled" : ""}
                                nextClassName={currentGraph === graphs.length - 1 ? "disabled" : ""}
                            />
                        </div>
                    </motion.div>
                </div>

                <div className="stats-sidebar">
                    <motion.div 
                        className="stats-card"
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.5, delay: 0.2 }}
                    >
                        <h3>Total Expenses</h3>
                        <p className="stats-value negative">
                            ${stats.totalExpenses.toFixed(2)}
                        </p>
                    </motion.div>

                    <motion.div 
                        className="stats-card"
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.5, delay: 0.3 }}
                    >
                        <h3>Total Income</h3>
                        <p className="stats-value positive">
                            ${stats.totalIncome.toFixed(2)}
                        </p>
                    </motion.div>

                    <motion.div 
                        className="stats-card"
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.5, delay: 0.4 }}
                    >
                        <h3>Most Expensive Category</h3>
                        {stats.mostExpensiveCategory ? (
                            <>
                                <p className="category-name">
                                    {stats.mostExpensiveCategory.name}
                                </p>
                                <p className="stats-value negative">
                                    ${stats.mostExpensiveCategory.value.toFixed(2)}
                                </p>
                            </>
                        ) : (
                            <p>No expenses yet</p>
                        )}
                    </motion.div>
                </div>
            </div>
        </div>
    );
};

export default Analytics;
