import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Calendar, Plus } from 'lucide-react';
import axios from 'axios';
import { motion } from 'framer-motion';
import jwt_decode from 'jwt-decode'; // Import JWT decoding library
import '../styles/Goals.css';
import AddGoalModal from '../componenets/AddGoalModal';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [currentBudget, setCurrentBudget] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          navigate('/login');
          return;
        }

        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000',
          headers: { Authorization: `Bearer ${token}` },
        });

        const [budgetRes, transactionsRes, goalsRes] = await Promise.all([
          axiosInstance.get('/budget'),
          axiosInstance.get('/transactions'),
          axiosInstance.get('/goals'),
        ]);

        const initialBudget = budgetRes.data.initial_budget;
        const transactionsTotal = transactionsRes.data.reduce((sum, t) => sum + t.amount, 0);
        const calculatedCurrentBudget = initialBudget + transactionsTotal;

        setCurrentBudget(calculatedCurrentBudget);
        setGoals(goalsRes.data);
        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to load data');
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleAddGoal = async (newGoal) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      // Decode the JWT token to get the user_id
      const decodedToken = jwt_decode(token);
      const userId = decodedToken.user.user_id; // Adjust according to your token structure
      const createdDate = new Date().toISOString(); // Current date in ISO format

      // Send the new goal data with user_id, created_date, and other goal details
      const response = await axios.post(
        '/add-goal',
        {
          user_id: userId,  // Adding user_id to the payload
          name: newGoal.name,
          target_amount: parseFloat(newGoal.target_amount),
          due_date: newGoal.due_date,
          created_date: createdDate, // Add created date
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // Append the new goal to the existing goals list and close the modal
      setGoals((prevGoals) => [...prevGoals, response.data]);
      setShowModal(false);
    } catch (error) {
      setError('Failed to add goal');
    }
  };

  if (loading) return <div className="loading-container">Loading...</div>;
  if (error) return <div className="error-container">{error}</div>;

  return (
    <div className="container">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="goals-list">
          {/* Add Goal Card */}
          <div className="goal-card add-goal-card" onClick={() => setShowModal(true)}>
            <div className="add-goal-content">
              <div className="add-goal-icon">
                <Plus size={24} />
              </div>
              <h3 className="add-goal-text">Add New Goal</h3>
            </div>
          </div>

          {/* Existing Goals */}
          {goals.length === 0 ? (
            <div className="empty-state">No goals available.</div>
          ) : (
            goals.map((goal) => {
              const isAchievable = currentBudget >= goal.target_amount;
              const progress = Math.min((currentBudget / goal.target_amount) * 100, 100);

              return (
                <div key={goal.id} className="goal-card">
                  <div className="goal-header">
                    <h3 className="goal-title">{goal.name}</h3>
                    <div className="goal-date">
                      <Calendar size={16} />
                      <span>{new Date(goal.due_date).toLocaleDateString()}</span>
                    </div>
                  </div>

                  <div className="goal-amounts">
                    <div className="amount-section">
                      <span className="amount-label">Target Amount</span>
                      <span
                        className={`amount-value ${isAchievable ? 'amount-achievable' : 'amount-not-achievable'}`}
                      >
                        ${goal.target_amount.toFixed(2)}
                      </span>
                    </div>
                    <div className="amount-section">
                      <span className="amount-label">Progress</span>
                      <span className="amount-value">
                        {progress.toFixed(1)}%
                      </span>
                    </div>
                  </div>

                  <div className="progress-bar-wrapper">
                    <div
                      className={`progress-bar ${isAchievable ? 'progress-achievable' : ''}`}
                      style={{ width: `${progress}%` }}
                    ></div>
                  </div>

                  <div className="goal-status">
                    {isAchievable ? (
                      <span className="status-achievable">Goal is achievable with current budget</span>
                    ) : (
                      <span className="status-not-achievable">
                        Need ${(goal.target_amount - currentBudget).toFixed(2)} more to reach goal
                      </span>
                    )}
                  </div>
                </div>
              );
            })
          )}
        </div>
      </motion.div>

      {/* Add Goal Modal */}
      <AddGoalModal
        showModal={showModal}
        setShowModal={setShowModal}
        handleAddGoal={handleAddGoal}
      />
    </div>
  );
};

export default Goals;
