// components/Navbar/Navbar.jsx
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Home, 
  Receipt, 
  BarChart2, 
  Target, 
  User,
  Menu,
  X
} from 'lucide-react';
import '../styles/Navbar';

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { path: '/', icon: <Home size={24} />, text: 'Dashboard' },
    { path: '/transactions', icon: <Receipt size={24} />, text: 'Transactions' },
    { path: '/analytics', icon: <BarChart2 size={24} />, text: 'Analytics' },
    { path: '/goals', icon: <Target size={24} />, text: 'Goals' },
  ];

  return (
    <>
      {/* Desktop Sidebar */}
      <nav className="desktop-nav">
        <div className="profile-section">
          <Link to="/profile" className={`nav-item ${location.pathname === '/profile' ? 'active' : ''}`}>
            <User size={24} />
            <span className="nav-text">Profile</span>
          </Link>
        </div>
        <div className="nav-items">
          {navItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
            >
              {item.icon}
              <span className="nav-text">{item.text}</span>
            </Link>
          ))}
        </div>
      </nav>

      {/* Mobile Navbar */}
      <nav className="mobile-nav">
        <button className="menu-toggle" onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
          {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
        
        <Link to="/profile" className="mobile-profile">
          <User size={24} />
        </Link>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="mobile-menu">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`mobile-nav-item ${location.pathname === item.path ? 'active' : ''}`}
                onClick={() => setIsMobileMenuOpen(false)}
              >
                {item.icon}
                <span>{item.text}</span>
              </Link>
            ))}
          </div>
        )}
      </nav>
    </>
  );
};

export default Navbar;
