import React, { useState, useEffect } from "react";
import axios from "axios";
import '../styles/Goals.css';

const Goals = () => {
    const [goals, setGoals] = useState([]);

    useEffect(() => {
        const fetchGoals = async () => {
            const token = localStorage.getItem("authToken"); // Assuming the token is stored in localStorage after login
            if (!token) {
                console.error("No token found");
                return;
            }
    
            try {
                const response = await axios.get("/goals", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setGoals(response.data);
            } catch (error) {
                console.error("Error fetching goals", error);
            }
        };
    
        fetchGoals();
    }, []);
    

    return (
        <div className="goals-container">
            {goals.length === 0 ? (
                <p>No goals available. Start adding some!</p>
            ) : (
                goals.map((goal) => (
                    <div key={goal.id} className="goal">
                        <div className="goal-header">
                            <h3>{goal.name}</h3>
                            <p className="due-date">Due: {new Date(goal.due_date).toLocaleDateString()}</p>
                        </div>
                        <div className="goal-body">
                            <p>Target Amount: ${goal.target_amount}</p>
                            <p>Current Amount: ${goal.current_amount || 0}</p>
                        </div>
                        <div className="goal-footer">
                            <button className="edit-button">Edit</button>
                            <button className="delete-button">Delete</button>
                        </div>
                    </div>
                ))
            )}
        </div>
    );
};

export default Goals;
