import React, { useState } from 'react';
import axios from 'axios';
import '../styles/GoalModal.css';

const AddGoalModal = ({ isOpen, onClose, onGoalAdd }) => {
    const [name, setName] = useState('');
    const [targetAmount, setTargetAmount] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [error, setError] = useState('');

    if (!isOpen) return null;

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const token = localStorage.getItem('authToken');
            if (!token) {
                setError('You are not logged in.');
                return;
            }

            const axiosInstance = axios.create({
                baseURL: 'http://localhost:5000',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            // Extract user_id from token
            const user_id = JSON.parse(atob(token.split('.')[1])).user.user_id; // Direct extraction from JWT payload

            const createdDate = new Date().toISOString(); // Current date in ISO format

            // Send the new goal data to the backend
            const response = await axiosInstance.post('/add-goal', {
                user_id,
                name,
                target_amount: parseFloat(targetAmount),
                due_date: dueDate,
                created_date: createdDate,
            });

            if (response.data.success) {
                onGoalAdd();
                onClose();  // Close the modal
                setName('');
                setTargetAmount('');
                setDueDate('');
            }
        } catch (error) {
            setError(error.response?.data?.error || 'Failed to add goal');
        }
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h2>Add Goal</h2>
                {error && <p className="error-message">{error}</p>}

                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Name</label>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Target Amount</label>
                        <input
                            type="number"
                            step="0.01"
                            value={targetAmount}
                            onChange={(e) => setTargetAmount(e.target.value)}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Due Date</label>
                        <input
                            type="date"
                            value={dueDate}
                            onChange={(e) => setDueDate(e.target.value)}
                            required
                        />
                    </div>

                    <div className="modal-actions">
                        <button type="button" onClick={onClose}>Cancel</button>
                        <button type="submit">Add Goal</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AddGoalModal;
