// Goals.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Calendar } from 'lucide-react';
import axios from 'axios';
import { motion } from 'framer-motion';
import '../styles/Goals.css';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchGoals = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          navigate('/login');
          return;
        }

        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000',
          headers: { Authorization: `Bearer ${token}` },
        });

        const response = await axiosInstance.get('/goals');
        setGoals(response.data);
        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to load goals');
        setLoading(false);
      }
    };

    fetchGoals();
  }, [navigate]);

  if (loading) return <div className="loading-container">Loading...</div>;
  if (error) return <div className="error-container">{error}</div>;

  return (
    <div className="container">
      <motion.div 
        className="recent-transactions"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h3>Your Financial Goals</h3>
        {goals.length === 0 ? (
          <div className="empty-state">No goals available.</div>
        ) : (
          <div className="goals-cards">
            {goals.map((goal) => (
              <div key={goal.id} className="transaction-card">
                <div className="card-header-with-icon">
                  <h3 className="goal-title">{goal.name}</h3>
                  <div className="goal-date">
                    <Calendar size={16} />
                    {new Date(goal.due_date).toLocaleDateString()}
                  </div>
                </div>
                
                <div className="detail-row">
                  <span className="detail-label">Current</span>
                  <span className="detail-value">
                    ${(goal.initial_amount || 0).toFixed(2)}
                  </span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Target</span>
                  <span className="detail-value">
                    ${(goal.target_amount || 0).toFixed(2)}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default Goals;
