import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Goals.css';
import axiosInstance from '../utils/axiosInstance';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchGoals = async () => {
      try {
        const response = await axiosInstance.get('/goals');
        setGoals(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch goals');
        setLoading(false);
        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchGoals();
  }, [navigate]);

  if (loading) {
    return <div className="loading-message">Loading goals...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  const totalTargetAmount = goals.reduce((sum, goal) => sum + parseFloat(goal.target_amount), 0);
  const totalCurrentAmount = goals.reduce((sum, goal) => sum + (parseFloat(goal.current_amount) || 0), 0);
  const overallProgress = (totalCurrentAmount / totalTargetAmount) * 100 || 0;

  return (
    <div className="goals-container">
      <div className="stats-container">
        <div className="stat-card">
          <h3>Total Goals</h3>
          <p>{goals.length}</p>
        </div>

        <div className="stat-card">
          <h3>Target Amount</h3>
          <p>${totalTargetAmount.toLocaleString('en-US', { minimumFractionDigits: 2 })}</p>
        </div>

        <div className="stat-card">
          <h3>Overall Progress</h3>
          <p>{overallProgress.toFixed(1)}%</p>
        </div>
      </div>

      <div className="goals-grid">
        <h2>Your Financial Goals</h2>
        
        {goals.length === 0 ? (
          <div className="no-goals-message">
            No goals available. Start by adding some goals!
          </div>
        ) : (
          <div className="goals-list">
            {goals.map((goal) => {
              const progress = ((goal.current_amount || 0) / goal.target_amount) * 100;
              
              return (
                <div key={goal.id} className="goal-card">
                  <div className="goal-header">
                    <h3>{goal.name}</h3>
                    <span className="goal-date">
                      {new Date(goal.due_date).toLocaleDateString()}
                    </span>
                  </div>
                  
                  <div className="goal-progress">
                    <div className="progress-info">
                      <span>Progress</span>
                      <span>{progress.toFixed(1)}%</span>
                    </div>
                    <div className="progress-bar">
                      <div 
                        className="progress-fill"
                        style={{ width: `${progress}%` }}
                      />
                    </div>
                  </div>
                  
                  <div className="goal-amounts">
                    <div className="amount-current">
                      <p>Current</p>
                      <p>${(goal.current_amount || 0).toLocaleString('en-US', { minimumFractionDigits: 2 })}</p>
                    </div>
                    <div className="amount-target">
                      <p>Target</p>
                      <p>${parseFloat(goal.target_amount).toLocaleString('en-US', { minimumFractionDigits: 2 })}</p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div className="pagination">
        <button className="pagination-button">Previous</button>
        <button className="pagination-button active">1</button>
        <button className="pagination-button">2</button>
        <button className="pagination-button">Next</button>
      </div>
    </div>
  );
};

export default Goals;
