import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import { motion } from 'framer-motion';

const HomePage = () => {
  const [budgetData, setBudgetData] = useState({
    current_budget: 0,
    initial_budget: 0
  });
  const [username, setUsername] = useState('Not logged in');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [animatedBudget, setAnimatedBudget] = useState({ current: 0, initial: 0 });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBudget = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          navigate('/login');
          return;
        }

        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000'
        });

        const response = await axiosInstance.get('/budget', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setBudgetData(response.data);
        setUsername(response.data.username);
        setLoading(false);

        // Start the budget animation
        animateBudget(response.data.current_budget, response.data.initial_budget);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to load budget information');
        setLoading(false);
        
        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchBudget();
  }, [navigate]);

  const animateBudget = (current, initial) => {
    const duration = 1500; // Animation duration in ms
    const steps = 60; // Number of steps in animation
    const currentStep = current / steps;
    const initialStep = initial / steps;
    let currentCount = 0;
    let initialCount = 0;
    let step = 0;

    const interval = setInterval(() => {
      if (step < steps) {
        currentCount += currentStep;
        initialCount += initialStep;
        setAnimatedBudget({
          current: currentCount,
          initial: initialCount
        });
        step++;
      } else {
        clearInterval(interval);
        setAnimatedBudget({
          current: current,
          initial: initial
        });
      }
    }, duration / steps);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your budget...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-container">{error}</div>;
  }

  return (
    <div className="container">
      <motion.div 
        className="budget-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card-header">
          <h1>Welcome back, {username}!</h1>
        </div>
        
        <div className="budget-info">
          <div className="budget-item">
            <h2>Current Budget</h2>
            <motion.p 
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
            >
              ${animatedBudget.current.toFixed(2)}
            </motion.p>
          </div>

          <div className="budget-item">
            <h2>Initial Budget</h2>
            <motion.p 
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
            >
              ${animatedBudget.initial.toFixed(2)}
            </motion.p>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default HomePage;
