import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
    LineChart, Line, XAxis, YAxis, 
    Tooltip, ResponsiveContainer, 
    PieChart, Pie, Cell, Legend 
} from 'recharts';

const Analytics = () => {
    const [data, setData] = useState({
        transactions: [],
        budget: 0,
        loading: true,
        error: null
    });

    const [view, setView] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    window.location.href = '/login';
                    return;
                }

                const [transactionsRes, budgetRes] = await Promise.all([
                    axios.get('/api/transactions', { 
                        headers: { Authorization: `Bearer ${token}` } 
                    }),
                    axios.get('/api/budget', { 
                        headers: { Authorization: `Bearer ${token}` } 
                    })
                ]);

                setData({
                    transactions: transactionsRes.data,
                    budget: budgetRes.data.initial_budget,
                    loading: false,
                    error: null
                });
            } catch (error) {
                setData(prev => ({
                    ...prev,
                    loading: false,
                    error: error.message
                }));
            }
        };

        fetchData();
    }, []);

    // Process transaction data
    const processedData = {
        timeline: data.transactions
            .sort((a, b) => new Date(a.date) - new Date(b.date))
            .map((t, index, arr) => ({
                date: new Date(t.date).toLocaleDateString(),
                balance: data.budget + arr.slice(0, index + 1)
                    .reduce((sum, transaction) => sum + transaction.amount, 0)
            })),
        categories: (() => {
            const expenseCategories = data.transactions
                .filter(t => t.amount < 0)
                .reduce((acc, t) => {
                    const category = t.category || 'Other';
                    acc[category] = (acc[category] || 0) + Math.abs(t.amount);
                    return acc;
                }, {});

            return Object.entries(expenseCategories)
                .map(([name, value]) => ({ name, value }))
                .sort((a, b) => b.value - a.value);
        })()
    };

    const stats = {
        totalExpenses: Math.abs(data.transactions
            .filter(t => t.amount < 0)
            .reduce((sum, t) => sum + t.amount, 0)),
        totalIncome: data.transactions
            .filter(t => t.amount > 0)
            .reduce((sum, t) => sum + t.amount, 0),
        mostExpensiveCategory: processedData.categories[0]
    };

    const COLORS = ['#751d6c', '#e74c3c', '#27ae60', '#3498db', '#f1c40f'];

    if (data.loading) return <div>Loading...</div>;
    if (data.error) return <div>Error: {data.error}</div>;

    const views = [
        {
            title: 'Budget Timeline',
            chart: (
                <ResponsiveContainer width="100%" height={300}>
                    <LineChart data={processedData.timeline}>
                        <XAxis dataKey="date" />
                        <YAxis tickFormatter={v => `$${v}`} />
                        <Tooltip />
                        <Line 
                            type="monotone" 
                            dataKey="balance" 
                            stroke="#751d6c" 
                            strokeWidth={2} 
                        />
                    </LineChart>
                </ResponsiveContainer>
            )
        },
        {
            title: 'Expense Categories',
            chart: (
                <ResponsiveContainer width="100%" height={300}>
                    <PieChart>
                        <Pie
                            data={processedData.categories}
                            cx="50%"
                            cy="50%"
                            innerRadius="60%"
                            outerRadius="80%"
                            fill="#8884d8"
                            dataKey="value"
                            label={({ name, percent }) => 
                                percent > 0.05 ? `${name} (${(percent * 100).toFixed(0)}%)` : ''
                            }
                        >
                            {processedData.categories.map((entry, index) => (
                                <Cell 
                                    key={`cell-${index}`} 
                                    fill={COLORS[index % COLORS.length]} 
                                />
                            ))}
                        </Pie>
                        <Tooltip />
                        <Legend />
                    </PieChart>
                </ResponsiveContainer>
            )
        }
    ];

    return (
        <div className="container">
            <div className="chart-section">
                <div className="chart-header">
                    <h2>{views[view].title}</h2>
                    <div className="chart-nav">
                        <button 
                            onClick={() => setView(0)} 
                            className={view === 0 ? 'active' : ''}
                        >
                            Timeline
                        </button>
                        <button 
                            onClick={() => setView(1)} 
                            className={view === 1 ? 'active' : ''}
                        >
                            Categories
                        </button>
                    </div>
                </div>
                {views[view].chart}
            </div>

            <div className="stats-section">
                <div className="stat-card">
                    <h3>Total Expenses</h3>
                    <p className="stat-value negative">${stats.totalExpenses.toFixed(2)}</p>
                </div>
                <div className="stat-card">
                    <h3>Total Income</h3>
                    <p className="stat-value positive">${stats.totalIncome.toFixed(2)}</p>
                </div>
                {stats.mostExpensiveCategory && (
                    <div className="stat-card">
                        <h3>Top Expense Category</h3>
                        <p className="stat-category">{stats.mostExpensiveCategory.name}</p>
                        <p className="stat-value negative">
                            ${stats.mostExpensiveCategory.value.toFixed(2)}
                        </p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Analytics;
