import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import { motion } from 'framer-motion';

const HomePage = () => {
const [profileImage, setProfileImage] = useState(null);


  const [budgetData, setBudgetData] = useState({
    current_budget: 0,
    initial_budget: 0
  });
  const [username, setUsername] = useState('Guest');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [animatedBudget, setAnimatedBudget] = useState({ current: 0, initial: 0 });
  const [transactions, setTransactions] = useState([]); // State for transactions
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          navigate('/login');
          return;
        }

        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000',
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // Fetch budget data
        const budgetResponse = await axiosInstance.get('/budget');
        setBudgetData(budgetResponse.data);
        setUsername(budgetResponse.data.username || 'Guest');
        animateBudget(budgetResponse.data.current_budget, budgetResponse.data.initial_budget);

        // Fetch transactions
        const transactionsResponse = await axiosInstance.get('/transactions');
        setTransactions(transactionsResponse.data);

        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to load data');
        setLoading(false);

        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchData();
  }, [navigate]);

  const animateBudget = (current, initial) => {
    const duration = 1500;
    const steps = 60;
    const currentStep = current / steps;
    const initialStep = initial / steps;
    let currentCount = 0;
    let initialCount = 0;
    let step = 0;

    const interval = setInterval(() => {
      if (step < steps) {
        currentCount += currentStep;
        initialCount += initialStep;
        setAnimatedBudget({
          current: currentCount,
          initial: initialCount
        });
        step++;
      } else {
        clearInterval(interval);
        setAnimatedBudget({
          current: current,
          initial: initial
        });
      }
    }, duration / steps);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-container">{error}</div>;
  }

  return (
    <div className="container">
      <div className="dashboard-header">
  <h1>Finance Dashboard</h1>
  <div className="user-profile">
    <div className="profile-pic">
      {/* You can replace this with an actual image */}
      {username.charAt(0).toUpperCase()}
    </div>
    <p>Welcome back, {username}!</p>
  </div>
</div>

      <motion.div 
        className="budget-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card-header">
          <h1>Your Budget Overview</h1>
        </div>
        
        <div className="budget-info">
          <div className="budget-item">
            <h2>Current Budget</h2>
            <motion.p 
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
            >
              ${animatedBudget.current.toFixed(2)}
            </motion.p>
          </div>

          <div className="budget-item">
            <h2>Initial Budget</h2>
            <motion.p 
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
            >
              ${animatedBudget.initial.toFixed(2)}
            </motion.p>
          </div>
        </div>
      </motion.div>

      {/* Transactions Table */}
      <div className="transactions-table">
        <h2>Recent Transactions</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Description</th>
                <th>Category</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction.id}>
                  <td>{new Date(transaction.date).toLocaleDateString()}</td>
                  <td>{transaction.description}</td>
                  <td>{transaction.category}</td>
                  <td style={{ color: transaction.type === 'income' ? 'green' : 'red' }}>
                    ${transaction.amount.toFixed(2)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
