import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Goals.css';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newGoal, setNewGoal] = useState({ name: '', target_amount: '', due_date: '' });
  const [editGoal, setEditGoal] = useState({ id: null, name: '', target_amount: '', due_date: '' });
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  // Fetch goals from the backend
  useEffect(() => {
    const fetchGoals = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get('http://localhost:5000/goals', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setGoals(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch goals');
        setLoading(false);
        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchGoals();
  }, [navigate]);

  // Handle adding a new goal
  const handleAddGoal = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const response = await axios.post(
        '/add-goal',
        {
          name: newGoal.name,
          target_amount: parseFloat(newGoal.target_amount),
          due_date: newGoal.due_date,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setGoals([...goals, response.data]);
      setNewGoal({ name: '', target_amount: '', due_date: '' });
    } catch (error) {
      setError('Failed to add goal');
    }
  };

  // Handle editing a goal
  const handleEditGoal = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const response = await axios.put(
        `/edit-goal/${editGoal.id}`,
        {
          name: editGoal.name,
          target_amount: parseFloat(editGoal.target_amount),
          due_date: editGoal.due_date,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const updatedGoals = goals.map((goal) =>
        goal.id === editGoal.id ? response.data : goal
      );
      setGoals(updatedGoals);
      setIsEditing(false);
      setEditGoal({ id: null, name: '', target_amount: '', due_date: '' });
    } catch (error) {
      setError('Failed to update goal');
    }
  };

  // Handle deleting a goal
  const handleDeleteGoal = async (id) => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/delete-goal/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const updatedGoals = goals.filter((goal) => goal.id !== id);
      setGoals(updatedGoals);
    } catch (error) {
      setError('Failed to delete goal');
    }
  };

  // Handle input changes for adding a new goal
  const handleNewGoalChange = (e) => {
    const { name, value } = e.target;
    setNewGoal({ ...newGoal, [name]: value });
  };

  // Handle input changes for editing a goal
  const handleEditGoalChange = (e) => {
    const { name, value } = e.target;
    setEditGoal({ ...editGoal, [name]: value });
  };

  // Start editing a goal
  const startEditing = (goal) => {
    setIsEditing(true);
    setEditGoal({
      id: goal.id,
      name: goal.name,
      target_amount: goal.target_amount,
      due_date: goal.due_date,
    });
  };

  if (loading) {
    return <div className="loading">Loading goals...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="goals-container">
      <h1>Your Goals</h1>

      {/* Add Goal Form */}
      <form onSubmit={isEditing ? handleEditGoal : handleAddGoal} className="goal-form">
        <input
          type="text"
          name="name"
          placeholder="Goal Name"
          value={isEditing ? editGoal.name : newGoal.name}
          onChange={isEditing ? handleEditGoalChange : handleNewGoalChange}
          required
        />
        <input
          type="number"
          name="target_amount"
          placeholder="Target Amount"
          value={isEditing ? editGoal.target_amount : newGoal.target_amount}
          onChange={isEditing ? handleEditGoalChange : handleNewGoalChange}
          required
        />
        <input
          type="date"
          name="due_date"
          value={isEditing ? editGoal.due_date : newGoal.due_date}
          onChange={isEditing ? handleEditGoalChange : handleNewGoalChange}
          required
        />
        <button type="submit">{isEditing ? 'Update Goal' : 'Add Goal'}</button>
        {isEditing && (
          <button type="button" onClick={() => setIsEditing(false)}>
            Cancel
          </button>
        )}
      </form>

      {/* Display Goals */}
      <div className="goals-list">
        {goals.length === 0 ? (
          <p>No goals available. Start adding some!</p>
        ) : (
          goals.map((goal) => (
            <div key={goal.id} className="goal-card">
              <div className="goal-header">
                <h3>{goal.name}</h3>
                <p className="due-date">Due: {new Date(goal.due_date).toLocaleDateString()}</p>
              </div>
              <div className="goal-body">
                <p>Target Amount: ${goal.target_amount}</p>
                <p>Current Amount: ${goal.current_amount || 0}</p>
              </div>
              <div className="goal-footer">
                <button onClick={() => startEditing(goal)}>Edit</button>
                <button onClick={() => handleDeleteGoal(goal.id)}>Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Goals;
