import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [budgetData, setBudgetData] = useState({
    current_budget: 0,
    initial_budget: 0
  });
  const [username, setUsername] = useState({username: 'Not logged in'})
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  

  useEffect(() => {
    const fetchBudget = async () => {
      try {
        const token = localStorage.getItem('authToken');
        console.log('Token:', token); // Debug token
        
        if (!token) {
          console.log('No token found, redirecting to login');
          navigate('/login');
          return;
        }

        // Create instance with interceptor for debugging
        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000'
        });

        axiosInstance.interceptors.request.use(request => {
          console.log('Request:', request);
          return request;
        });

        axiosInstance.interceptors.response.use(
          response => {
            console.log('Response:', response);
            return response;
          },
          error => {
            console.log('Error Response:', error.response);
            return Promise.reject(error);
          }
        );

        const response = await axiosInstance.get('/budget', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        console.log('Budget data:', response.data); // Debug response
        setBudgetData(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Detailed error:', error.response || error);
        setError(error.response?.data?.error || 'Failed to load budget information');
        setLoading(false);
        
        if (error.response?.status === 401) {
          console.log('Unauthorized, redirecting to login');
          navigate('/login');
        }
      }
    };

    fetchBudget();
  }, [navigate]);

  // Rest of your component remains the same
  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  if (error) {
    return <div className="flex justify-center items-center h-screen text-red-500">{error}</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-8">Finance Dashboard</h1>
          <div className="mb-4">
            <p className="text-sm text-gray-500">User is authenticated</p>
          </div>
        </div>
        
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-2">
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <dt className="text-sm font-medium text-gray-500">Initial Budget</dt>
              <dd className="mt-1 text-3xl font-semibold text-gray-900">
                ${budgetData.initial_budget?.toFixed(2) || '0.00'}
              </dd>
            </div>
          </div>

          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <dt className="text-sm font-medium text-gray-500">Current Budget</dt>
              <dd className="mt-1 text-3xl font-semibold text-gray-900">
                ${budgetData.current_budget?.toFixed(2) || '0.00'}
              </dd>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
