import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import { motion } from 'framer-motion';
import { Wallet, TrendingDown, TrendingUp, Calendar, ClockIcon } from 'lucide-react';

const HomePage = () => {
  const [budgetData, setBudgetData] = useState({
    current_budget: 0,
    initial_budget: 0,
  });
  const [username, setUsername] = useState('Guest');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [animatedBudget, setAnimatedBudget] = useState({ current: 0, initial: 0 });
  const [transactions, setTransactions] = useState([]);
  const navigate = useNavigate();

  const baseCurrentBudgetRef = useRef(0);
  const processedTransactionsRef = useRef(new Set());

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:3001');
    console.log('ðŸ”„ Attempting WebSocket connection...');

    ws.onopen = () => {
      console.log('ðŸŸ¢ Connected to WebSocket');
      const userId = JSON.parse(localStorage.getItem('user')).id;
      console.log('ðŸ‘¤ Sending user identification:', userId);
      ws.send(JSON.stringify({
        type: 'identify',
        userId: userId,
      }));
    };

    ws.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data);
        console.log('ðŸ“© Received WebSocket message:', message);
    
        switch (message.type) {
          case 'transaction_update':
            console.log('ðŸ’¸ Received transaction update:', message.data);
    
            if (message.data.action === 'add') {
              const newTransaction = message.data.transaction;

              // Check if transaction has already been processed to prevent double-counting
              if (processedTransactionsRef.current.has(newTransaction.id)) {
                console.log('Transaction already processed, skipping');
                return;
              }

              // Add transaction to processed set
              processedTransactionsRef.current.add(newTransaction.id);
    
              // Update transactions state
              setTransactions((prevTransactions) => {
                // Avoid duplicates
                if (prevTransactions.some(t => t.id === newTransaction.id)) {
                  return prevTransactions;
                }
    
                // Add the new transaction
                return [newTransaction, ...prevTransactions];
              });

              // Update current_budget 
              setBudgetData((prevBudgetData) => {
                const newCurrentBudget = baseCurrentBudgetRef.current + newTransaction.amount;
                
                // Update the base current budget ref for next calculation
                baseCurrentBudgetRef.current = newCurrentBudget;

                // Animate the budget
                animateBudget(newCurrentBudget, prevBudgetData.initial_budget);

                return {
                  ...prevBudgetData,
                  current_budget: newCurrentBudget,
                };
              });
            }
            break;
    
          default:
            console.log('â„¹ï¸ Unhandled message type:', message.type);
        }
      } catch (error) {
        console.error('âŒ Error processing WebSocket message:', error);
      }
    };

    ws.onerror = (error) => {
      console.error('âŒ WebSocket error:', error);
    };

    ws.onclose = () => {
      console.log('ðŸ”´ Disconnected from WebSocket');
    };

    return () => {
      console.log('ðŸ§¹ Cleaning up WebSocket connection...');
      if (ws.readyState === WebSocket.OPEN) {
        ws.close();
      }
    };
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          navigate('/login');
          return;
        }

        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Fetch budget data
        const budgetResponse = await axiosInstance.get('/budget');
        const initialBudget = budgetResponse.data.initial_budget;
        setUsername(budgetResponse.data.username || 'Guest');

        // Fetch transactions
        const transactionsResponse = await axiosInstance.get('/transactions');
        
        // Create a set of transaction IDs to track processed transactions
        processedTransactionsRef.current = new Set(transactionsResponse.data.map(t => t.id));
        
        setTransactions(transactionsResponse.data);

        // Calculate current budget correctly
        const totalTransactions = transactionsResponse.data.reduce((sum, t) => sum + t.amount, 0);
        const initialCalculatedBudget = initialBudget + totalTransactions;

        // Set the base current budget ref
        baseCurrentBudgetRef.current = initialCalculatedBudget;

        // Set budget data with correct calculation
        setBudgetData({
          initial_budget: initialBudget,
          current_budget: initialCalculatedBudget,
        });

        // Animate budget
        animateBudget(initialCalculatedBudget, initialBudget);

        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to load data');
        setLoading(false);

        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchData();
  }, [navigate]);

  const animateBudget = (current, initial) => {
    const duration = 1500;
    const steps = 60;
    const currentStep = current / steps;
    const initialStep = initial / steps;
    let currentCount = animatedBudget.current;
    let initialCount = animatedBudget.initial;
    let step = 0;

    const interval = setInterval(() => {
      if (step < steps) {
        currentCount += currentStep;
        initialCount += initialStep;
        setAnimatedBudget({
          current: currentCount,
          initial: initialCount,
        });
        step++;
      } else {
        clearInterval(interval);
        setAnimatedBudget({
          current: current,
          initial: initial,
        });
      }
    }, duration / steps);
  };

  // Helper functions
  const getEarliestTransaction = () => {
    return transactions.length > 0
      ? transactions.reduce((earliest, t) => (t.date < earliest.date ? t : earliest))
      : null;
  };

  const getLatestTransaction = () => {
    return transactions.length > 0
      ? transactions.reduce((latest, t) => (t.date > latest.date ? t : latest))
      : null;
  };

  const getTopRecentTransactions = () => {
    return transactions
      .sort((a, b) => new Date(b.date) - new Date(a.date))
      .slice(0, 3);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-container">{error}</div>;
  }

  return (
    <div className="container">
      <motion.div
        className="budget-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card-header">
          <h1>Your Budget Overview</h1>
        </div>

        <div className="budget-info">
          <div className="budget-item">
            <h2>Current Budget</h2>
            <motion.p
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
            >
              {budgetData.current_budget !== null ? `$${budgetData.current_budget.toFixed(2)}` : '-'}
            </motion.p>
          </div>

          <div className="budget-item">
            <h2>Initial Budget</h2>
            <motion.p
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
            >
              {budgetData.initial_budget !== null ? `$${budgetData.initial_budget.toFixed(2)}` : '-'}
            </motion.p>
          </div>
        </div>

        {/* Budget Progress Bar */}
        <div className="progress-bar-container">
          <div
            className={`progress-bar ${
              (animatedBudget.current / animatedBudget.initial) < 0.5 ? 'progress-danger' : ''
            }`}
            style={{
              width: `${(animatedBudget.current / animatedBudget.initial) * 100}%`,
            }}
          ></div>
        </div>
      </motion.div>

      {/* Transaction Summary Cards */}
      <motion.div 
        className="transaction-summary-container"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        <h2 className="summary-header">Transaction Overview</h2>
        <div className="transaction-summary-cards">
          <div className="summary-card expenses">
            <div className="summary-icon-wrapper expense-icon">
              <TrendingDown size={24} />
            </div>
            <div className="summary-content">
              <h3>Total Expenses</h3>
              <p className="summary-amount">
                ${Math.abs(transactions
                  .filter(t => t.amount < 0)
                  .reduce((sum, t) => sum + t.amount, 0))
                  .toFixed(2)}
              </p>
              <p className="summary-label">total spent</p>
            </div>
          </div>

          <div className="summary-card income">
            <div className="summary-icon-wrapper income-icon">
              <TrendingUp size={24} />
            </div>
            <div className="summary-content">
              <h3>Total Income</h3>
              <p className="summary-amount">
                ${transactions
                  .filter(t => t.amount > 0)
                  .reduce((sum, t) => sum + t.amount, 0)
                  .toFixed(2)}
              </p>
              <p className="summary-label">total earned</p>
            </div>
          </div>

          <div className="summary-card">
            <div className="summary-icon-wrapper calendar-icon">
              <Calendar size={24} />
            </div>
            <div className="summary-content">
              <h3>Earliest Transaction</h3>
              <p className="summary-amount">
                {getEarliestTransaction() ? new Date(getEarliestTransaction().date).toLocaleDateString() : '-'}
              </p>
            </div>
          </div>

          <div className="summary-card">
            <div className="summary-icon-wrapper clock-icon">
              <ClockIcon size={24} />
            </div>
            <div className="summary-content">
              <h3>Latest Transaction</h3>
              <p className="summary-amount">
                {getLatestTransaction() ? new Date(getLatestTransaction().date).toLocaleDateString() : '-'}
              </p>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Recent Transactions */}
      <motion.div
        className="recent-transactions-container"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
      >
        <h2 className="transactions-header">Recent Transactions</h2>
        <ul className="transactions-list">
          {getTopRecentTransactions().map((transaction) => (
            <li key={transaction.id} className="transaction-item">
              <div className="transaction-item-header">
                <p>{transaction.category}</p>
                <p>{new Date(transaction.date).toLocaleDateString()}</p>
              </div>
              <p className="transaction-amount">{transaction.amount}</p>
            </li>
          ))}
        </ul>
      </motion.div>
    </div>
  );
};

export default HomePage;
