import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import { motion } from 'framer-motion';

const HomePage = () => {
  const [budgetData, setBudgetData] = useState({
    current_budget: 0,
    initial_budget: 0,
  });
  const [username, setUsername] = useState('Guest');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [animatedBudget, setAnimatedBudget] = useState({ current: 0, initial: 0 });
  const [transactions, setTransactions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          navigate('/login');
          return;
        }

        const axiosInstance = axios.create({
          baseURL: 'http://localhost:5000',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Fetch budget data
        const budgetResponse = await axiosInstance.get('/budget');
        setBudgetData(budgetResponse.data);
        setUsername(budgetResponse.data.username || 'Guest');

        // Fetch transactions
        const transactionsResponse = await axiosInstance.get('/transactions');
        setTransactions(transactionsResponse.data);

        // Calculate current budget
        const totalOutgoing = transactionsResponse.data
          .filter((t) => t.amount < 0) // Filter outgoing transactions (negative amounts)
          .reduce((sum, t) => sum + t.amount, 0); // Sum all outgoing transactions

        const currentBudget = budgetResponse.data.initial_budget + totalOutgoing; // Subtract outgoing from initial budget
        setBudgetData((prev) => ({ ...prev, current_budget: currentBudget }));

        // Animate budget
        animateBudget(currentBudget, budgetResponse.data.initial_budget);

        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to load data');
        setLoading(false);

        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchData();
  }, [navigate]);

  const animateBudget = (current, initial) => {
    const duration = 1500;
    const steps = 60;
    const currentStep = current / steps;
    const initialStep = initial / steps;
    let currentCount = 0;
    let initialCount = 0;
    let step = 0;

    const interval = setInterval(() => {
      if (step < steps) {
        currentCount += currentStep;
        initialCount += initialStep;
        setAnimatedBudget({
          current: currentCount,
          initial: initialCount,
        });
        step++;
      } else {
        clearInterval(interval);
        setAnimatedBudget({
          current: current,
          initial: initial,
        });
      }
    }, duration / steps);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-container">{error}</div>;
  }

  // Helper functions
  const getEarliestTransaction = () => {
    return transactions.length > 0
      ? transactions.reduce((earliest, t) => (t.date < earliest.date ? t : earliest))
      : null;
  };

  const getLatestTransaction = () => {
    return transactions.length > 0
      ? transactions.reduce((latest, t) => (t.date > latest.date ? t : latest))
      : null;
  };

  const getTopRecentTransactions = () => {
    return transactions
      .sort((a, b) => new Date(b.date) - new Date(a.date)) // Sort by date (most recent first)
      .slice(0, 3); // Get top 3
  };

  return (
    <div className="container">
      <motion.div
        className="budget-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card-header">
          <h1>Your Budget Overview</h1>
        </div>

        <div className="budget-info">
          <div className="budget-item">
            <h2>Current Budget</h2>
            <motion.p
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.3 }}
            >
              ${animatedBudget.current.toFixed(2)}
            </motion.p>
          </div>

          <div className="budget-item">
            <h2>Initial Budget</h2>
            <motion.p
              className="budget-amount"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5 }}
            >
              ${animatedBudget.initial.toFixed(2)}
            </motion.p>
          </div>
        </div>

        {/* Budget Progress Bar */}
        <div className="progress-bar-container">
          <div
            className="progress-bar"
            style={{
              width: `${(animatedBudget.current / animatedBudget.initial) * 100}%`,
            }}
          ></div>
        </div>
      </motion.div>

      {/* Earliest and Latest Transactions */}
      <div className="transaction-cards">
        <div className="transaction-card">
          <h3>Earliest Transaction</h3>
          {getEarliestTransaction() ? (
            <>
              <p>Date: {getEarliestTransaction().date}</p>
              <p>Amount: ${getEarliestTransaction().amount.toFixed(2)}</p>
              <p>Description: {getEarliestTransaction().description}</p>
            </>
          ) : (
            <p>No transactions found.</p>
          )}
        </div>

        <div className="transaction-card">
          <h3>Latest Transaction</h3>
          {getLatestTransaction() ? (
            <>
              <p>Date: {getLatestTransaction().date}</p>
              <p>Amount: ${getLatestTransaction().amount.toFixed(2)}</p>
              <p>Description: {getLatestTransaction().description}</p>
            </>
          ) : (
            <p>No transactions found.</p>
          )}
        </div>
      </div>

      {/* Top 3 Recent Transactions Table */}
      <div className="recent-transactions">
        <h3>Recent Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Amount</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {getTopRecentTransactions().map((t) => (
              <tr key={t.id}>
                <td>{t.date}</td>
                <td>${t.amount.toFixed(2)}</td>
                <td>{t.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default HomePage;
