import React, { useState } from 'react';
import '../styles/AddGoalModal.css'

const AddGoalModal = ({ showModal, setShowModal, handleAddGoal }) => {
  const [newGoal, setNewGoal] = useState({ name: '', target_amount: '', due_date: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    handleAddGoal(newGoal);
    setNewGoal({ name: '', target_amount: '', due_date: '' }); // Reset form
  };

  if (!showModal) return null;

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h3>Add New Goal</h3>
        
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Goal Name</label>
            <input
              type="text"
              value={newGoal.name}
              onChange={(e) => setNewGoal({ ...newGoal, name: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Target Amount</label>
            <input
              type="number"
              value={newGoal.target_amount}
              onChange={(e) => setNewGoal({ ...newGoal, target_amount: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Due Date</label>
            <input
              type="date"
              value={newGoal.due_date}
              onChange={(e) => setNewGoal({ ...newGoal, due_date: e.target.value })}
              required
            />
          </div>
          <div className="modal-actions">
            <button type="button" className="cancel-button" onClick={() => setShowModal(false)}>
              Cancel
            </button>
            <button type="submit" className="submit-button">
              Add Goal
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddGoalModal;
