// Transactions.jsx
import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {useNavigate} from 'react-router-dom';
import {motion} from 'framer-motion';
import '../styles/Transactions.css';

const Transactions = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchTransactions = async () => {
            try {
                const token = localStorage.getItem('authToken');
                if (!token) {
                    navigate('/login');
                    return;
                }

                const axiosInstance = axios.create({
                    baseURL: 'http://localhost:5000',
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                const response = await axiosInstance.get('/transactions');
                setTransactions(response.data);
                setLoading(false);
            } catch (error) {
                setError(error.response?.data?.error || 'Failed to load transactions');
                setLoading(false);
                if (error.response?.status === 401) {
                    navigate('/login');
                }
            }
        };

        fetchTransactions();
    }, [navigate]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Loading transactions...</p>
            </div>
        );
    }

    if (error) {
        return <div className="error-container">{error}</div>;
    }

    return(
        <div className="container">
            <motion.div 
                className="transactions-section"
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ duration: 0.5 }}
            >
                <div className="transactions-header">
                    <p className="transaction-count">Total transactions: {transactions.length}</p>
                </div>
                
                <div className="transactions-table-container">
                    <table>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Description</th>
                                <th>Category</th>
                                <th>Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            {transactions
                                .sort((a, b) => new Date(b.date) - new Date(a.date))
                                .map((transaction) => (
                                    <tr key={transaction.id}>
                                        <td>{transaction.date}</td>
                                        <td>{transaction.description}</td>
                                        <td>{transaction.category}</td>
                                        <td>
                                            <span className={transaction.amount < 0 ? 'table-amount-negative' : 'table-amount-positive'}>
                                                ${Math.abs(transaction.amount).toFixed(2)}
                                            </span>
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </motion.div>

            <motion.div
            className='action-container'
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.5 }}

            >
                



            </motion.div>
        </div>
    );
}

export default Transactions;
