import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
    PieChart, Pie, Cell, Legend
} from 'recharts';
import { TrendingUp, PieChart as PieChartIcon } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import '../styles/Analytics.css';
import ReactPaginate from 'react-paginate';

const Analytics = () => {
    const [transactions, setTransactions] = useState([]);
    const [budgetData, setBudgetData] = useState({ initial_budget: 0 });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [currentGraph, setCurrentGraph] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('authToken');
                if (!token) {
                    navigate('/login');
                    return;
                }

                const axiosInstance = axios.create({
                    baseURL: 'http://localhost:5000',
                    headers: { Authorization: `Bearer ${token}` },
                });

                const [transactionsRes, budgetRes] = await Promise.all([
                    axiosInstance.get('/transactions'),
                    axiosInstance.get('/budget')
                ]);

                setTransactions(transactionsRes.data);
                setBudgetData(budgetRes.data);
                setLoading(false);
            } catch (error) {
                setError(error.response?.data?.error || 'Failed to load data');
                setLoading(false);
            }
        };

        fetchData();
    }, [navigate]);

    // Prepare timeline data
    const timelineData = (() => {
        let currentBudget = budgetData.initial_budget;
        return transactions
            .sort((a, b) => new Date(a.date) - new Date(b.date))
            .map(t => {
                currentBudget += t.amount;
                return {
                    date: new Date(t.date).toLocaleDateString(),
                    budget: currentBudget,
                    transaction: t.description
                };
            });
    })();

    // Prepare category data
    const categoryData = (() => {
        const categoryTotals = transactions.reduce((acc, t) => {
            if (t.amount < 0) { // Only count expenses for category breakdown
                const category = t.category || 'Other';
                acc[category] = (acc[category] || 0) + Math.abs(t.amount);
            }
            return acc;
        }, {});

        return Object.entries(categoryTotals)
            .map(([name, value]) => ({ name, value }))
            .sort((a, b) => b.value - a.value); // Sort by value descending
    })();

    // Calculate stats
    const stats = (() => {
        const totalExpenses = Math.abs(
            transactions
                .filter(t => t.amount < 0)
                .reduce((sum, t) => sum + t.amount, 0)
        );
        const totalIncome = transactions
            .filter(t => t.amount > 0)
            .reduce((sum, t) => sum + t.amount, 0);

        return {
            totalExpenses,
            totalIncome,
            mostExpensiveCategory: categoryData[0] || null
        };
    })();

    if (loading) return <div className="loading-container">Loading...</div>;
    if (error) return <div className="error-container">{error}</div>;

    const COLORS = ['#751d6c', '#e74c3c', '#27ae60', '#3498db', '#f1c40f'];
    const graphs = [
        {
            title: "Budget Timeline",
            icon: <TrendingUp />,
            content: (
                <ResponsiveContainer width="100%" height="100%">
                    <LineChart data={timelineData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="date" />
                        <YAxis width={80} tickFormatter={value => `$${value}`} />
                        <Tooltip 
                            content={({ active, payload }) => {
                                if (active && payload && payload.length) {
                                    return (
                                        <div className="custom-tooltip">
                                            <p>Date: {payload[0].payload.date}</p>
                                            <p>Budget: ${payload[0].value.toFixed(2)}</p>
                                            <p>Transaction: {payload[0].payload.transaction}</p>
                                        </div>
                                    );
                                }
                                return null;
                            }}
                        />
                        <Line 
                            type="monotone" 
                            dataKey="budget" 
                            stroke="#751d6c" 
                            strokeWidth={2}
                            dot={false}
                        />
                    </LineChart>
                </ResponsiveContainer>
            )
        },
        {
            title: "Expense Categories",
            icon: <PieChartIcon />,
            content: (
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                        <Pie
                            data={categoryData}
                            cx="50%"
                            cy="50%"
                            innerRadius={60}
                            outerRadius={120}
                            paddingAngle={5}
                            dataKey="value"
                            labelLine={false}
                            label={({ name, percent }) => {
                                return percent > 0.05 ? `${name} (${(percent * 100).toFixed(0)}%)` : '';
                            }}
                        >
                            {categoryData.map((entry, index) => (
                                <Cell 
                                    key={`cell-${index}`} 
                                    fill={COLORS[index % COLORS.length]} 
                                />
                            ))}
                        </Pie>
                        <Tooltip 
                            formatter={(value) => `$${Number(value).toFixed(2)}`}
                            contentStyle={{
                                background: 'white',
                                border: 'none',
                                borderRadius: '8px',
                                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
                            }}
                        />
                        <Legend 
                            verticalAlign="bottom"
                            align="center"
                            layout="horizontal"
                            height={36}
                        />
                    </PieChart>
                </ResponsiveContainer>
            )
        }
    ];

    return (
        <div className="analytics-container">
            <div className="analytics-content">
                <motion.div 
                    className="analytics-card"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <div className="graph-header">
                        <div className="graph-title">
                            {graphs[currentGraph].icon}
                            <h2>{graphs[currentGraph].title}</h2>
                        </div>
                        <div className="pagination-wrapper">
                            <ReactPaginate
                                previousLabel="Previous"
                                nextLabel="Next"
                                pageCount={graphs.length}
                                onPageChange={({ selected }) => setCurrentGraph(selected)}
                                containerClassName="pagination"
                                activeClassName="active"
                                previousClassName={currentGraph === 0 ? "disabled" : ""}
                                nextClassName={currentGraph === graphs.length - 1 ? "disabled" : ""}
                                pageClassName="page-item"
                                pageLinkClassName="page-link"
                                breakClassName="break"
                                breakLinkClassName="break-link"
                            />
                        </div>
                    </div>
                    <div className="chart-container">
                        {graphs[currentGraph].content}
                    </div>
                </motion.div>

                <div className="budget-overview-container">
                    <motion.div 
                        className="info-card"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.1 }}
                    >
                        <h3>Initial Budget</h3>
                        <p className="budget-value">${budgetData.initial_budget.toFixed(2)}</p>
                    </motion.div>

                    <motion.div 
                        className="info-card"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.2 }}
                    >
                        <h3>Spent Amount</h3>
                        <p className="spent-value">${stats.totalExpenses.toFixed(2)}</p>
                    </motion.div>

                    <motion.div 
                        className="info-card"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.3 }}
                    >
                        <h3>Remaining Budget</h3>
                        <p className="remaining-value">${(budgetData.initial_budget - stats.totalExpenses).toFixed(2)}</p>
                    </motion.div>

                    <motion.div 
                        className="info-card"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.4 }}
                    >
                        <h3>Total Income</h3>
                        <p className="income-value">${stats.totalIncome.toFixed(2)}</p>
                    </motion.div>

                    <motion.div 
                        className="info-card"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.5 }}
                    >
                        <h3>Most Expensive Category</h3>
                        {stats.mostExpensiveCategory ? (
                            <>
                                <p className="category-name">{stats.mostExpensiveCategory.name}</p>
                                <p className="category-value">${stats.mostExpensiveCategory.value.toFixed(2)}</p>
                            </>
                        ) : (
                            <p>No expenses yet</p>
                        )}
                    </motion.div>
                </div>
            </div>
        </div>
    );
};

export default Analytics;
