import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Target, Calendar, DollarSign, TrendingUp } from 'lucide-react';
import axios from 'axios';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Fetch goals from the backend
  useEffect(() => {
    const fetchGoals = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get('http://localhost:5000/goals', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setGoals(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch goals');
        setLoading(false);
        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchGoals();
  }, [navigate]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg text-gray-600">Loading goals...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg text-red-600">{error}</div>
      </div>
    );
  }

  // Calculate total target amount and current progress
  const totalTargetAmount = goals.reduce((sum, goal) => sum + parseFloat(goal.target_amount), 0);
  const totalCurrentAmount = goals.reduce((sum, goal) => sum + (parseFloat(goal.current_amount) || 0), 0);
  const overallProgress = (totalCurrentAmount / totalTargetAmount) * 100 || 0;

  return (
    <div className="p-5 max-w-7xl mx-auto font-sans">
      {/* Header Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-5 mb-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center gap-3 mb-3">
            <Target className="w-6 h-6 text-purple-800" />
            <h3 className="text-lg font-medium text-gray-800">Total Goals</h3>
          </div>
          <p className="text-2xl font-bold text-purple-800">{goals.length}</p>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center gap-3 mb-3">
            <DollarSign className="w-6 h-6 text-purple-800" />
            <h3 className="text-lg font-medium text-gray-800">Target Amount</h3>
          </div>
          <p className="text-2xl font-bold text-green-500">
            ${totalTargetAmount.toLocaleString('en-US', { minimumFractionDigits: 2 })}
          </p>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center gap-3 mb-3">
            <TrendingUp className="w-6 h-6 text-purple-800" />
            <h3 className="text-lg font-medium text-gray-800">Overall Progress</h3>
          </div>
          <p className="text-2xl font-bold text-blue-500">
            {overallProgress.toFixed(1)}%
          </p>
        </div>
      </div>

      {/* Goals Grid */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <div className="flex items-center gap-3 mb-6">
          <Target className="w-6 h-6 text-purple-800" />
          <h2 className="text-xl font-semibold text-gray-800">Your Financial Goals</h2>
        </div>

        {goals.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            No goals available. Start by adding some goals!
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {goals.map((goal) => {
              const progress = ((goal.current_amount || 0) / goal.target_amount) * 100;
              
              return (
                <div key={goal.id} className="bg-gray-50 rounded-lg p-6 hover:shadow-lg transition-shadow">
                  <div className="flex justify-between items-start mb-4">
                    <h3 className="text-lg font-semibold text-gray-800">{goal.name}</h3>
                    <div className="flex items-center text-sm text-gray-500">
                      <Calendar className="w-4 h-4 mr-1" />
                      {new Date(goal.due_date).toLocaleDateString()}
                    </div>
                  </div>
                  
                  <div className="space-y-3">
                    <div>
                      <div className="flex justify-between text-sm text-gray-600 mb-1">
                        <span>Progress</span>
                        <span>{progress.toFixed(1)}%</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-purple-800 rounded-full h-2 transition-all duration-500"
                          style={{ width: `${progress}%` }}
                        />
                      </div>
                    </div>
                    
                    <div className="flex justify-between items-center">
                      <div>
                        <p className="text-sm text-gray-500">Current</p>
                        <p className="text-lg font-semibold text-gray-800">
                          ${(goal.current_amount || 0).toLocaleString('en-US', { minimumFractionDigits: 2 })}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-gray-500">Target</p>
                        <p className="text-lg font-semibold text-gray-800">
                          ${parseFloat(goal.target_amount).toLocaleString('en-US', { minimumFractionDigits: 2 })}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* Pagination */}
      <div className="flex justify-center">
        <div className="flex gap-2">
          <button className="px-4 py-2 rounded bg-gray-100 hover:bg-gray-200 transition-colors">
            Previous
          </button>
          <button className="px-4 py-2 rounded bg-purple-800 text-white">
            1
          </button>
          <button className="px-4 py-2 rounded bg-gray-100 hover:bg-gray-200 transition-colors">
            2
          </button>
          <button className="px-4 py-2 rounded bg-gray-100 hover:bg-gray-200 transition-colors">
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default Goals;
