import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
    PieChart, Pie, Cell, Legend
} from 'recharts';
import { TrendingUp, PieChart as PieChartIcon } from 'lucide-react';
import ReactPaginate from 'react-paginate';
import '../styles/Analytics.css';
import { useNavigate } from 'react-router-dom';

const Analytics = () => {
    const [transactions, setTransactions] = useState([]);
    const [budgetData, setBudgetData] = useState({ initial_budget: 0 });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [currentPage, setCurrentPage] = useState(0);

    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('authToken');
                if (!token) {
                    navigate('/login');
                    return;
                }

                const axiosInstance = axios.create({
                    baseURL: 'http://localhost:5000',
                    headers: { Authorization: `Bearer ${token}` },
                });

                // Fetch transactions and budget data
                const [transactionsRes, budgetRes] = await Promise.all([
                    axiosInstance.get('/transactions'),
                    axiosInstance.get('/budget')
                ]);

                setTransactions(transactionsRes.data);
                setBudgetData(budgetRes.data);
                setLoading(false);
            } catch (error) {
                setError(error.response?.data?.error || 'Failed to load data');
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const prepareBudgetTimelineData = () => {
        let currentBudget = budgetData.initial_budget;
        return transactions
            .sort((a, b) => new Date(a.date) - new Date(b.date))
            .map(t => {
                currentBudget += t.amount;
                return {
                    date: new Date(t.date).toLocaleDateString(),
                    budget: currentBudget,
                    transaction: t.description
                };
            });
    };

    const prepareCategoryData = () => {
        const categoryTotals = transactions.reduce((acc, t) => {
            const category = t.category || 'Other';
            acc[category] = (acc[category] || 0) + Math.abs(t.amount);
            return acc;
        }, {});

        return Object.entries(categoryTotals).map(([name, value]) => ({
            name,
            value
        }));
    };

    const calculateStats = () => {
        const totalExpenses = Math.abs(
            transactions
                .filter(t => t.amount < 0)
                .reduce((sum, t) => sum + t.amount, 0)
        );
        const totalIncome = transactions
            .filter(t => t.amount > 0)
            .reduce((sum, t) => sum + t.amount, 0);

        const mostExpensiveCategory = Object.entries(
            transactions
                .filter(t => t.amount < 0)
                .reduce((acc, t) => {
                    acc[t.category] = (acc[t.category] || 0) + Math.abs(t.amount);
                    return acc;
                }, {})
        ).sort(([,a], [,b]) => b - a)[0];

        return {
            totalExpenses,
            totalIncome,
            mostExpensiveCategory: mostExpensiveCategory ? {
                category: mostExpensiveCategory[0],
                amount: mostExpensiveCategory[1]
            } : null
        };
    };

    const handlePageChange = ({ selected }) => {
        setCurrentPage(selected);
    };

    if (loading) return <div className="loading-container">Loading...</div>;
    if (error) return <div className="error-container">{error}</div>;

    const timelineData = prepareBudgetTimelineData();
    const categoryData = prepareCategoryData();
    const stats = calculateStats();
    const COLORS = ['#751d6c', '#e74c3c', '#27ae60', '#3498db', '#f1c40f'];

    const graphs = [
        {
            title: "Budget Timeline",
            content: (
                <motion.div 
                    className="chart-container"
                    initial={{ opacity: 0, y: 50 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8 }}
                >
                    <ResponsiveContainer width="100%" height={300}>
                        <LineChart data={timelineData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="date" />
                            <YAxis />
                            <Tooltip 
                                content={({ active, payload }) => {
                                    if (active && payload && payload.length) {
                                        return (
                                            <div className="custom-tooltip">
                                                <p>Date: {payload[0].payload.date}</p>
                                                <p>Budget: ${payload[0].value.toFixed(2)}</p>
                                                <p>Transaction: {payload[0].payload.transaction}</p>
                                            </div>
                                        );
                                    }
                                    return null;
                                }}
                            />
                            <Line 
                                type="monotone" 
                                dataKey="budget" 
                                stroke="#751d6c" 
                                dot={false}
                            />
                        </LineChart>
                    </ResponsiveContainer>
                </motion.div>
            )
        },
        {
            title: "Spending by Category",
            content: (
                <motion.div 
                    className="chart-container"
                    initial={{ opacity: 0, y: 50 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8 }}
                >
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie
                                data={categoryData}
                                cx="50%"
                                cy="50%"
                                outerRadius={100}
                                fill="#8884d8"
                                dataKey="value"
                                label={({ name, percent }) => 
                                    `${name} ${(percent * 100).toFixed(0)}%`
                                }
                            >
                                {categoryData.map((entry, index) => (
                                    <Cell 
                                        key={`cell-${index}`} 
                                        fill={COLORS[index % COLORS.length]} 
                                    />
                                ))}
                            </Pie>
                            <Tooltip 
                                formatter={(value) => `$${value.toFixed(2)}`}
                            />
                            <Legend />
                        </PieChart>
                    </ResponsiveContainer>
                </motion.div>
            )
        }
    ];

    return (
        <div className="container">
            <div className="graph-stats-container">
                {/* Graphs Section */}
                <div className="graph-container">
                    {graphs[currentPage].content}
                </div>

                {/* Stats Cards Section */}
                <div className="stats-container">
                    <div className="stats-card">
                        <h3>Total Expenses</h3>
                        <p className="stats-value negative">
                            ${stats.totalExpenses.toFixed(2)}
                        </p>
                    </div>
                    <div className="stats-card">
                        <h3>Total Income</h3>
                        <p className="stats-value positive">
                            ${stats.totalIncome.toFixed(2)}
                        </p>
                    </div>
                    <div className="stats-card">
                        <h3>Most Expensive Category</h3>
                        {stats.mostExpensiveCategory ? (
                            <>
                                <p className="stats-category">
                                    {stats.mostExpensiveCategory.category}
                                </p>
                                <p className="stats-value negative">
                                    ${stats.mostExpensiveCategory.amount.toFixed(2)}
                                </p>
                            </>
                        ) : (
                            <p>No expenses yet</p>
                        )}
                    </div>
                </div>
            </div>

            {/* Pagination Controls Below */}
            <div className="pagination-container">
                <ReactPaginate
                    previousLabel={"Previous"}
                    nextLabel={"Next"}
                    breakLabel={"..."}
                    pageCount={graphs.length}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={handlePageChange}
                    containerClassName={"pagination"}
                    activeClassName={"active"}
                />
            </div>
        </div>
    );
};

export default Analytics;
